$(document).ready(function() {
  $("body").addClass("js");

  var $menu = $("#menu"),
    	$menulink = $(".menu-link"),
    	$wrap = $("#content"),
    	$nav = $("nav"),
   		$link = $("nav a");

  $menulink.click(function() {
    $menulink.toggleClass("active");
    $wrap.toggleClass("active");
    $nav.toggleClass("active");
    return false;
	});

  $link.click(function() {
  	$menulink.removeClass("active");
    $wrap.removeClass("active");
    $nav.removeClass("active");
  });

});