$(document).ready(function() {
  $('body').addClass('js');

  var $menu = $('#menu'),
    $menulink = $('.menu-link'),
    $wrap = $('#content');
    $nav = $('nav');

  $menulink.click(function() {
    $menulink.toggleClass('active');
    $wrap.toggleClass('active');
    $nav.toggleClass('active');
    return false;
	});
});